jobjectArray SettingsList(JNIEnv *env, jobject activityObject) {
    jobjectArray ret;

    const char *features[] = {
            "Category_Settings",
            "-1_Toggle_Save feature preferences", //-1 is checked on Preferences.java
            "-3_Toggle_Auto size vertically",
            "Category_Menu",
            "-6_Button_<font color='red'>Close settings</font>",
    };

    //Now you dont have to manually update the number everytime;
    int Total_Feature = (sizeof features / sizeof features[0]);
    ret = env->NewObjectArray(Total_Feature,
                              env->FindClass("java/lang/String"),
                              env->NewStringUTF(""));

    for (int i = 0; i < Total_Feature; i++)
        env->SetObjectArrayElement(ret, i, env->NewStringUTF(features[i]));

    settingsValid = true;

    return (ret);
}
